import React, { useState, useEffect } from "react";
import wallpaper1 from "../Assets/wallpaper1.jpg";
import wallpaper2 from "../Assets/wallpaper2.jpg";
import wallpaper3 from "../Assets/wallpaper3.jpg";
import "./SliderSection.css";

const images = [
  { src: wallpaper1, description: "Deskripsi gambar 1" },
  { src: wallpaper2, description: "Deskripsi gambar 2" },
  { src: wallpaper3, description: "Deskripsi gambar 3" },
];

const SliderSection = () => {
  const [index, setIndex] = useState(0);
  const [selectedImage, setSelectedImage] = useState(images[0]);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prevIndex) => (prevIndex + 1) % images.length);
      setSelectedImage(images[(index + 1) % images.length]);
    }, 3000);
    return () => clearInterval(interval);
  }, [index]);

  const handleClick = (image) => {
    setSelectedImage(image);
  };

  return (
    <div className="slider-section">
      <div className="slider">
        <div
          className="slides"
          style={{ transform: `translateX(${-index * 100}%)` }}
        >
          {images.map((image, i) => (
            <img
              key={i}
              src={image.src}
              alt={`Slide ${i}`}
              onClick={() => handleClick(image)}
            />
          ))}
        </div>
      </div>
      <div className="image-description">
        <p>{selectedImage.description}</p>
      </div>
    </div>
  );
};

export default SliderSection;
