import React, { useState, useRef } from "react";
import Slider from "react-slick";
import { showModal, closeModal } from "./modall.js";
import "./SliderSection.css";

const SliderSection = () => {
  const Data = [
    {
      id: 1,
      title: "Tempe Mendoan",
      image: "/src/Assets/image1.jpg",
      description: "Tempe mendoan adalah hidangan khas dari Jawa Tengah...",
    },
    {
      id: 2,
      title: "Lumpia Semarang",
      image: "/src/Assets/image2.jpg",
      description: "Lumpia Semarang adalah hidangan khas dari Semarang...",
    },
    {
      id: 3,
      title: "Wajik Magelang",
      image: "/src/Assets/image3.jpg",
      description: "Wajik Magelang adalah makanan khas dari Magelang...",
    },
    // Tambahkan data lainnya sesuai kebutuhan
  ];

  const [activeCard, setActiveCard] = useState(null);
  const sliderRef = useRef(null);

  const toggleDescription = (id, title, description, imageUrl) => {
    setActiveCard(activeCard === id ? null : id);
    if (activeCard !== id) {
      showModal(title, description, imageUrl);
    } else {
      closeModal();
    }
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    pauseOnHover: true,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 2,
        },
      },
    ],
  };

  return (
    <div className="ccontainer">
      <h2>Makanan Khas Jawa Tengah</h2>
      <Slider {...settings} ref={sliderRef}>
        {Data.map((item) => (
          <div
            key={item.id}
            className="card"
            onClick={() => toggleDescription(item.id, item.title, item.description, item.image)}
          >
            <img src={item.image} alt={item.title} />
            <div className="click-me">Click Me</div>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default SliderSection;
