import React, { useState, useEffect } from "react";
import "./styles.css";

const apiKey = 'AIzaSyAu7vyp9P6KKQzo4awfHcRS2cw4Aj5i4Fs';
const playlistId = 'PL46GhFqJIVbYE6k1rf-ajz1zRj7ab9DFX';
const maxResults = 50;

const PlaylistSection = () => {
  const [items, setItems] = useState([]);
  const [prevPageToken, setPrevPageToken] = useState('');
  const [nextPageToken, setNextPageToken] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [currentPageTokens, setCurrentPageTokens] = useState([]);
  const [currentPageIndex, setCurrentPageIndex] = useState(0);

  useEffect(() => {
    fetchPlaylistItems();
  }, []);

  const fetchPlaylistItems = async (pageToken = '') => {
    const response = await fetch(
      `https://www.googleapis.com/youtube/v3/playlistItems?part=snippet&maxResults=${maxResults}&playlistId=${playlistId}&key=${apiKey}&pageToken=${pageToken}`
    );
    const data = await response.json();
    setItems(data.items);
    setPrevPageToken(data.prevPageToken || '');
    setNextPageToken(data.nextPageToken || '');

    const newPageTokens = [...currentPageTokens];
    newPageTokens[currentPageIndex] = pageToken;
    setCurrentPageTokens(newPageTokens);
  };

  const handleNextPage = () => {
    if (nextPageToken) {
      setCurrentPageIndex(currentPageIndex + 1);
      fetchPlaylistItems(nextPageToken);
    }
  };

  const handlePrevPage = () => {
    if (currentPageIndex > 0) {
      setCurrentPageIndex(currentPageIndex - 1);
      fetchPlaylistItems(currentPageTokens[currentPageIndex - 1]);
    }
  };

  const displayResults = (itemsToDisplay) => {
    return itemsToDisplay.map((item) => (
      <div className="video" key={item.snippet.resourceId.videoId}>
        <h3>{item.snippet.title}</h3>
        <img
          src={item.snippet.thumbnails.medium.url}
          alt={item.snippet.title}
          onClick={() => playVideo(item.snippet.resourceId.videoId)}
        />
      </div>
    ));
  };

  const playVideo = (videoId) => {
    setSelectedVideo(videoId);
  };

  const handleSearch = (e) => {
    setSearchQuery(e.target.value.toLowerCase());
  };

  const filteredItems = items.filter((item) =>
    item.snippet.title.toLowerCase().includes(searchQuery)
  );

  return (
    <div id="playlist-section">
      <div id="search-container">
        <input
          type="text"
          id="search-input"
          placeholder="Search..."
          value={searchQuery}
          onChange={handleSearch}
        />
      </div>
      {selectedVideo && (
        <div id="video-player">
          <iframe
            id="player"
            width="560"
            height="315"
            src={`https://www.youtube.com/embed/${selectedVideo}?autoplay=1`}
            frameBorder="0"
            allow="autoplay; encrypted-media"
            allowFullScreen
          ></iframe>
        </div>
      )}
      <div id="results">
        {displayResults(filteredItems)}
      </div>
      <div id="pagination">
        <button
          id="prev-button"
          onClick={handlePrevPage}
          disabled={currentPageIndex === 0}
        >
          Previous
        </button>
        <button
          id="next-button"
          onClick={handleNextPage}
          disabled={!nextPageToken}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default PlaylistSection;
